import React, { useEffect, useState } from 'react'
import { useDataContext } from '../hooks/useDataContext'
import { useNavigate, useParams } from 'react-router-dom'
import useFetch from '../handling/useFetch'

const Details = () => {

  const { id } = useParams()
  const { error, isPending, datalog } = useFetch('http://localhost:8080/api/datalog/' + id)
  const navigate = useNavigate()

  const { dispatch } = useDataContext()
  const [ name, setName ] = useState('')
  const [ facility, setFacility ] = useState('')
  const [ issue, setIssue ] = useState('')
  const [ status, setStatus ] = useState('')

  const remover = async () => {
    const response = await fetch('/api/datalog/' + id, {
      method: 'DELETE'
    })
    // const json = await response.json()
    if (response.ok) {
      // dispatch({type: 'delete_datalog', payload: json})
      navigate('/')
    }
  }

  return (
    <div className='create'>
    <form className='form'>
      <p>Post new issue</p>
      <label>Name</label>
      <input type='text' autoFocus required
      onChange={(e) => setName(e.target.value)}
      value={datalog.name}>
      </input>

      <label>Facility</label>
      <input type='text' required
      onChange={(e) => setFacility(e.target.value)}
      value={datalog.facility}>
      </input>

      <label>Issue</label>
      <textarea type='text' required
      onChange={(e) => setIssue(e.target.value)}
      value={datalog.issue}>
      </textarea>

      <label>Status</label>
      <select
      value={status}
      onChange={(e) => setStatus(e.target.value)}>
        <option value='' disabled>Select Status</option>
        <option value='MAINTENANCE'>Maintenance</option>
        <option value='DONE' disabled>Done</option>
      </select>

      {!isPending && <button>POST</button>}
      {isPending && <button disabled>POSTING ...</button>}
      {error && <div className='error'>{error}</div>}
    </form>
      <button onClick={remover}>Delete</button>
    </div>
  )
}

export default Details
