import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'

const Details = () => {

  const { id } = useParams()
  const [ name, setName ] = useState('')
  const [ facility, setFacility ] = useState('')
  const [ issue, setIssue ] = useState('')
  const [ status, setStatus ] = useState('')
  const [ error, setError ] = useState(null)
  const [ isPending, setPending ] = useState(false)
  const navigate = useNavigate()

  useEffect(() => {
    getDetails()
  })

  const getDetails = async () => {
    let result = await fetch(`http://localhost:8080/api/datalog/${id._id}`)
    result = await result.json()
    setName(result.name)
    setFacility(result.facility)
    setIssue(result.issue)
    setStatus(result.status)
  }

  const remover = async () => {
    const response = await fetch('/api/datalog/' + id, {
      method: 'DELETE'
    })
    // const json = await response.json()
    if (response.ok) {
      // dispatch({type: 'delete_datalog', payload: json})
      navigate('/')
    }
  }

  return (
    <div className='create'>
    <form className='form'>
      <p>Update issue</p>
      <label>Name</label>
      <input type='text' autoFocus required
      value={name}
      onChange={(e) => setName(e.target.value)}
      >
      </input>

      <label>Facility</label>
      <input type='text' required
      value={facility}
      onChange={(e) => setFacility(e.target.value)}>
      </input>

      <label>Issue</label>
      <textarea type='text' required
      value={issue}
      onChange={(e) => setIssue(e.target.value)}
      >
      </textarea>

      <label>Status</label>
      <select
      value={status}
      onChange={(e) => setStatus(e.target.value)}>
        <option value='' disabled>Select Status</option>
        <option value='MAINTENANCE'>Maintenance</option>
        <option value='DONE'>Done</option>
      </select>

      {!isPending && <button>UPDATE</button>}
      {isPending && <button disabled>UPDATING...</button>}
      {error && <div className='error'>{error}</div>}
    </form>
      <button onClick={remover}>Delete</button>
    </div>
  )
}

export default Details
