import React, {useState} from 'react'
import { useDataContext } from '../hooks/useDataContext'

const Show_tmp = ({datalog}) => {

    const { dispatch } = useDataContext()
    const [element, setElement] = useState(2)
    const [isLoad, setLoad] = useState(false)
    const [isEmpty, setEmpty] = useState(false)
    const load = () => {
      setElement(element + 2)
      if (element <= datalog.length) {
        setLoad(false)
      } else {
        setLoad(true)
      }
    }
    
    const slice = datalog.slice(0, element)

    const handclick = async () => {
    const response = await fetch('/api/datalog/' + datalog._id, {
      method: 'delete'
    })
    const json = await response.json()

    if (response.ok) {
      dispatch({type: 'delete_datalog', payload: json})
    }
  }

  return (
    <div className='show'>
        {slice.map((data) => (
            <div className='show-list'>
              <table>
                <tr>
                  <td>Register ID</td>
                  <td>:</td>
                  <td>{data.id}</td>
                </tr>
                <tr>
                  <td>Name</td>
                  <td>:</td>
                  <td>{data.name}</td>
                </tr>
                <tr>
                  <td>Facility</td>
                  <td>:</td>
                  <td>{data.facility}</td>
                </tr>
                <tr hidden>
                  <td>Issue</td>
                  <td>:</td>
                  <td>{data.issue}</td>
                </tr>
              </table>
              <button onClick={handclick}>X</button>
            </div>
        ))}
        {!isLoad && <button onClick={load} className='btn-loadmore'>Load more</button>}
        {isLoad && <p onClick={load} className='max-loadmore'>Reached maximum pages</p>}
        
    </div>
  )
}

export default Show_tmp
