import React, { useEffect, useState } from 'react'
import { useDataContext } from '../hooks/useDataContext'
import { useNavigate, useParams } from 'react-router-dom'
import useFetch from '../handling/useFetch'

const Details = () => {

  const { id } = useParams()
  const { datalog } = useFetch('http://localhost:8080/api/datalog/' + id)
  const { dispatch } = useDataContext()
  const [ name, setName ] = useState('')
  const [ facility, setFacility ] = useState('')
  const [ issue, setIssue ] = useState('')
  const [ status, setStatus ] = useState('')
  const [ error, setError ] = useState(null)
  const [ isPending, setPending ] = useState(false)
  const navigate = useNavigate()

  useEffect(() => {
    getDetails()
  }, [])

  const getDetails = async () => {
    console.warn(id);
  }

  /*const put = async(e) => {
    e.preventDefault()
    setPending(true)
    const datalog = {name, facility, issue, status}
    const response = await fetch('/api/datalog', {
      method: 'PUT',
      body: JSON.stringify(datalog),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const json = await response.json()

    if (!response.ok) {
      setError(json.error)
      setPending(false)
    }
    if (response.ok) {
      setName('')
      setFacility('')
      setIssue('')
      setStatus('')
      setError(null)
      setPending(false)
      navigate('/')
      console.log('created datalog', json)
      dispatch({
        type: 'create_datalog',
        payload: json,
      })
    }
  } */

  const remover = async () => {
    const response = await fetch('/api/datalog/' + id, {
      method: 'DELETE'
    })
    // const json = await response.json()
    if (response.ok) {
      // dispatch({type: 'delete_datalog', payload: json})
      navigate('/')
    }
  }

  return (
    <div className='create'>
    {datalog && (
    <form className='form'>
      <p>Update issue</p>
      <label>Name</label>
      <input type='text' autoFocus required
      onChange={(e) => setName(e.target.value)}
      value={datalog.name}>
      </input>

      <label>Facility</label>
      <input type='text' required
      value={datalog.facility}>
      </input>

      <label>Issue</label>
      <textarea type='text' required
      value={datalog.issue}>
      </textarea>

      <label>Status</label>
      <select
      value={datalog.status}>
        <option value='' disabled>Select Status</option>
        <option value='MAINTENANCE'>Maintenance</option>
        <option value='DONE'>Done</option>
      </select>

      {!isPending && <button>UPDATE</button>}
      {isPending && <button disabled>UPDATING...</button>}
      {error && <div className='error'>{error}</div>}
    </form>
    )}
      <button onClick={remover}>Delete</button>
    </div>
  )
}

export default Details
